import React, {useEffect, useState} from 'react';
import {Alert, SafeAreaView, View} from "react-native";
import {Button, Card, TextInput} from "react-native-paper";
import {loginStyle} from "./login.style";
import { useValidation } from 'react-native-form-validator';
interface LoginScreenProps {
    navigation: any;
}




const Login = (props: LoginScreenProps) => {

    const logar = () => props.navigation.navigate("Home")
    const register = () => props.navigation.navigate("Register")

    const [email, setEmail] = useState(null)
    const [password, setPassword] = useState(null)

    const [errorEmail, setErrorEmail] = useState(null)

    const { validate, isFieldInError, getErrorsInField, getErrorMessages } =
        useValidation({
            state: { email, password },
        });

    const validar = () => {
        validate({
            email: { email: true },
            password: { minlength: 3, maxlength: 7, required: true }
        });
    }


    return (
                <SafeAreaView style={loginStyle.content}>
                    <View style={loginStyle.view}>
                        <Card>
                            <Card.Title title="Mobility Center" titleStyle={loginStyle.cardTitle}></Card.Title>
                            <Card.Content>
                                <TextInput
                                    placeholder="Email"
                                    label="User"
                                    keyboardType="email-address"
                                    onChangeText={setEmail}
                                    value={email}

                                />
                                <TextInput
                                    label="Password"
                                    secureTextEntry={true}
                                    onChangeText={setPassword}
                                    value={password}
                                />
                                <Button uppercase={false} style={loginStyle.cardButton}>Esqueceu email/password?</Button>
                                <Button onPress={validar} mode="contained" style={loginStyle.cardButton}>Login</Button>
                                <Button onPress={register} style={loginStyle.cardButton}>Register</Button>
                            </Card.Content>
                        </Card>
                    </View>
                </SafeAreaView>
    );
}

export default Login;
